$(function () {
    "use strict";
    $(".invoice-repeater,.subaccount-repeater,.repeater-default").repeater({
        show: function () {
            $(this).slideDown(), feather && feather.replace({ width: 14, height: 14 });
        },
        hide: function (e) {
            //confirm("Are you sure you want to delete this element?") && $(this).slideUp(e);
          $(this).slideUp(e);
        },
    });
});
$("#add_new_additional_field").click(function () {
    $(".append-additional-field-row").removeClass("d-none");
});
$("#add_new_sub_account").click(function () {
    $(".append-sub-account-row").removeClass("d-none");
});
